mapper.select:
select bookmarkno as i, bookmarkname as n, url as u
from
bookmark
where
deleteflg = #{deleteflg,jdbcType=VARCHAR}
and hot =
#{hot,jdbcType=VARCHAR}
and userid = #{userid,jdbcType=VARCHAR}
order by
updatetime asc

select bookmarkno as i, bookmarkname as n, url as u from
bookmark
where deleteflg="1" and userid = #{userid,jdbcType=VARCHAR}

select group_concat(distinct T3.tagname,",") as tags, T2.description as description
from user_bind_tags T1, (
	select tags, description
	from bookmark
	where userid = #{userid,jdbcType=VARCHAR}
	and bookmarkno = #{bookmarkno,jdbcType=INTEGER}
) T2, tags T3
where T1.userid = #{userid,jdbcType=VARCHAR}
and find_in_set(T1.tagno, T2.tags)
and T1.tagid = T3.tagid
order by T1.tagno asc

mapper.update:
update
bookmark
set deleteflg = #{deleteflg,jdbcType=VARCHAR}
where bookmarkno
= #{bookmarkno,jdbcType=INTEGER}
and userid =
#{userid,jdbcType=VARCHAR}

mapper.delete:
delete from
bookmark
where bookmarkno = #{bookmarkno,jdbcType=INTEGER}
and userid =
#{userid,jdbcType=VARCHAR}

mapper.update:
update bookmark
set
deleteflg = #{deleteflg,jdbcType=VARCHAR}
where bookmarkno =
#{bookmarkno,jdbcType=INTEGER}
and userid = #{userid,jdbcType=VARCHAR}

update bookmark
set
bookmarkname = #{bookmarkname,jdbcType=VARCHAR},
url
= #{url,jdbcType=VARCHAR}

where bookmarkid = #{bookmarkid,jdbcType=INTEGER}

update.foreach:
update bookmark

	sort = #{bookmark.sort,jdbcType=INTEGER},
	categoryno =
	#{bookmark.categoryno,jdbcType=VARCHAR},
	updatetime =
	#{bookmark.updatetime,jdbcType=TIMESTAMP}

where bookmarkno = #{bookmark.bookmarkno,jdbcType=INTEGER}
and userid
= #{bookmark.userid,jdbcType=VARCHAR}

mapper.select:
select T1.bookmarkno as i, T1.bookmarkname as n, T1.url as u, T2.categoryname as cn
from bookmark T1, category T2
where T1.userid = #{userid,jdbcType=VARCHAR}
and T1.deleteflg = #{deleteflg,jdbcType=VARCHAR}
and find_in_set(#{tags,jdbcType=VARCHAR},T1.tags)
and T2.userid = #{userid,jdbcType=VARCHAR}
and T1.categoryno = T2.categoryno
order by T1.usetimes desc
